	scwin.btn_dynamicRequestData_json_onclick = function(){
	    // json 데이터를 전송하기 위해서는 mediaType을 application/json으로 설정을 해야 합니다.
	    $p.executeSubmission( "json_dataListWithMessge_02" , {"req_info":{"userID":"Dynamic_EDU003"}});

	};

