head.script:

	scwin.onpageload = function() {
		scwin.trigger1_onclick();
	};

	scwin.onpageunload = function() {

	};


	// data 조회
	scwin.trigger1_onclick = function(e) {
		var arr = [];

		for(var i=0;i<4;i++) {
			if(i==0) {
				arr.push("A");
			} else if(i==1) {
				arr.push("B");
			} else if(i==2) {
				arr.push("C");
			} else if(i==3) {
				arr.push("D");
			}

			for(var j=0;j<4;j++) {
				  arr.push( Math.floor(Math.random() * 1000) );
			}
		}

		dataList1.setData(arr);
		console.log("data 조회 완료....");
	};

	// data 초기화
	scwin.trigger2_onclick = function(e) {
		dataList1.removeAll();
		console.log("data 초기화 완료...");
	};

	// line chart
	scwin.trigger3_onclick = function(e) {
		var options = {};
  		options.fileName = "line.xlsx";
		options.type  = "1";
		options.showProcess = false;
		options.chart = {
			"chartName": "line",
			"chartType": "",
			"title": "차트 타이틀",
			"yTitle": "Y 타이틀",
			"xTitle": "X 타이틀",
			"startRowIndex": 0,
			"startColIndex": 5,
			"endRowIndex": 20,
			"endColIndex": 15,
			"serAxis": "true",
			"catAxis": "true",
			"catAxisRange": {
				"startColIndex": 1,  
				"startRowIndex": 0,
				"endColIndex": 4,
				"endRowInex": 0
			},
			"serAxisRange": {
				"startColIndex": 0,
				"startRowIndex": 1,
				"endColIndex": 0,
				"endRowIndex": 4
			},
			"dataAxisRange": {
				"startColIndex": 1,
				"startRowIndex": 1,
				"endColIndex": 4,
				"endRowIndex": 4
			},
			"catGridLines": "true",    // 가로
			"serGridLines": "true",    // 세로
			"legend": "true",          // 범례
			"legendPosition": "bottom" // 범례위치
		};

		var infoArr = [];
  		gridView1.advancedExcelDownload(options , infoArr);
	};

