head.script:
        
	scwin.onpageload = function() {
		scwin.addWidget("E01");
	};
	
	scwin.onpageunload = function() {
		
	};
	

	scwin.ui_addWidget_onviewchange = function(info) {
		//var curType = info.newValue;
		scwin.addWidget(info.newValue);
		this.setValue("");
	};
	
	scwin.addWidget = function(type){
		var wTitle, wSrc, wWidth=3, wHeight=3, wPath = "/ws5/sample/compList/widgetContent/", wId;
		
		switch(type){
			case "C01":
				wTitle = "기기 유형별 조회수";
				wSrc = wPath+"wcPage2.xml";
			break;
			case "C02":
				wTitle = "성별 조회수";
				wSrc = wPath+"wcPage3.xml";
			break;
			case "C03":
				wTitle = "일별 조회수";
				wSrc = wPath+"wcPage1.xml";
			break;
			case "E01":
				wTitle = "사용자 조회";
				wSrc = wPath+"wcPage4.xml";
				wHeight = 5;	
			break;
			case "E02":
				wTitle = "캘린더";
				wSrc = wPath+"wcPage5.xml";
				wHeight = 4;
				wWidth = 3;	
			break;
			default : return;
		}
		
		wId = "W"+type;
		
		if(ui_widget.getWidgetById( wId )){
			ui_widget.moveWidget( wId , 0 , 0 );	
		}
		
		var opt = {
			id : wId,
			src : wSrc,
			title : wTitle,
			x : 0,
			y : 0,
			scope : true,
			unitWidth : wWidth,
    		unitHeight : wHeight
		};
		
		//ui_widget.getWidgetById( id );
		
		ui_widget.addWidgets( opt );
		
	};
